CREATE DATABASE IF NOT EXISTS jalalia;

USE jalalia;

CREATE TABLE IF NOT EXISTS calendar (
    id INT AUTO_INCREMENT PRIMARY KEY,
    date DATE NOT NULL,
    fajr TIME NOT NULL,
    fajr_jamah TIME DEFAULT NULL,
    sunrise TIME DEFAULT NULL,
    dhuhr TIME NOT NULL,
    dhuhr_jamah TIME DEFAULT NULL,
    asr TIME NOT NULL,
    asr_jamah TIME DEFAULT NULL,
    sunset TIME NOT NULL,
    maghrib_jamah TIME DEFAULT NULL,
    isha TIME NOT NULL,
    isha_jamah TIME DEFAULT NULL,
    jummah TIME DEFAULT NULL,
    eid1 TIME DEFAULT NULL,
    eid2 TIME DEFAULT NULL
);

